import React from "react"
import Card from "./Card"
import { useState } from "react"
import data from "./data"
import { useEffect } from "react"


export default function Gallery() {

    const [cardData,setCardData] = useState(data)
    const [imgArray, setImgArray] = useState([])
    const [count, setCount] = useState(7)

    imgArray.length = count



    function renderGalleryItem(){
       fetch("https://api.unsplash.com/collections/907185/photos?page=10&per_page=60&client_id=GUOJSuCC3h67wLW1KgIFaXBbR43XKLZWKw4hSKpAkds")
        .then(response => response.json())
          .then(data => {
             setImgArray(data)
             console.log(data)
          })
      }

      renderGalleryItem()

      fetch("./data.json")
      .then(response => response.json())
      .then(data => {
            setCardData(data)
            console.log(data)
      })

      const dataCards = cardData.map(item => {
            return(
                <Card
                title={item.title}
                description={item.description}
                />
            )
      })

    const cards = imgArray.map(item => { 
        return (
        <Card
        key={item.id}
        alt={item.alt_description}
        url={item.urls.regular}
        description={item.description}
        />)

    })
    return (
        <div className="wrapper">
           <h3 className="wrapper-text">Connect people and spaces </h3> 
         {cards}
        </div>
    )
}
