import React from "react"
import Card from "./Card"
import { useState } from "react"
import data from "./data"
import { useEffect } from "react"


export default function Gallery() {

    const [cardData,setCardData] = useState(data)
    const [imgArray, setImgArray] = useState([])
    const [card, setCard] = useState()



    React.useEffect(() => {


       fetch("https://api.unsplash.com/collections/907185/photos?page=10&per_page=60&client_id=GUOJSuCC3h67wLW1KgIFaXBbR43XKLZWKw4hSKpAkds")
        .then(response => response.json())
          .then(data => {
             setImgArray(data)
             console.log(data)
          })


    }, [])

     function renderImgs() {
         for (let i = 0; i < 8; i++) {
            setCard(() => {
                return imgArray.map(item => {

                    return {...item, id: item.id}; 
                });
            });
        }
         console.log(card)
     }

     renderImgs()


    const cards = imgArray.map(item => { 
        return (
        <Card
        key={item.id}
        alt={item.alt_description}
        url={item.urls.regular}
        description={item.description}
        desc2={item.user.bio}
        title={item.user.name}
        />)

    })



    return (
        <div className="wrapper">
            <div className="box-text"><h3 className="wrapper-text">Connect people & spaces </h3> </div>
           <button className="wrapper-btn" >Button</button>
         {cards}
        </div>
    )
}
