    const [cardData,setCardData] = useState(data)
    const [allImg, setAllImg] = useState([])
    const [imgArray, setImgArray] = useState([])



    React.useEffect(() => {


       fetch("https://api.unsplash.com/collections/907185/photos?page=10&per_page=60&client_id=GUOJSuCC3h67wLW1KgIFaXBbR43XKLZWKw4hSKpAkds")
        .then(response => response.json())
          .then(data => {
             setAllImg(data)
             console.log(data)
          })


    }, [])

    function renderImages() {
        for (let i = 0; i < 8; i++) {
            setImgArray(imgArray.push(allImg[i])) 
        }
    }
    renderImages()
    console.log(allImg)



    const cards = imgArray.map(item => { 
        return (
        <Card
        key={item.id}
        alt={item.alt_description}
        url={item.urls.regular}
        description={item.description}
        desc2={item.user.bio}
        title={item.user.name}
        />)

    })

    const incrementCount = () => {
