import React from "react"
import Card from "./Card"
import { useState } from "react"
import data from "../data"
import { useEffect } from "react"


export default function Gallery() {

    const numItemsToGenerate = 7 //how many photos you want to display
    const [cardData,setCardData] = useState(data.card)
    const [imgArray, setImgArray] = useState([])

    function renderGalleryItem(randomNumber){
       fetch("https://api.unsplash.com/collections/907185/photos/?client_id=GUOJSuCC3h67wLW1KgIFaXBbR43XKLZWKw4hSKpAkds")
        .then(response => response.json())
          .then(data => {
             setImgArray(data)
             console.log(data)
          })
      }

      for(let i=0; i < numItemsToGenerate; i++){
          renderGalleryItem();
    }

    const cards = imgArray.map(item => { 
        return (
        <Card
        key={item.id}
        alt={item.alt_description}
        url={item.urls.regular}
        title={cardData.title}
        about={cardData.about}
        />)

    })
    return (
        <div className="wrapper">
           <h3 className="wrapper-text">Connect people and spaces</h3> 
         {cards}
        </div>
    )
}
